import './App.css';
import MenuItems from './components/Navbar/MenuItems';
import ReactDOM from 'react-dom'
import React, { useState } from 'react'
var request_sent = false;

function App() {

  const [selectedItem, setSelectedItem] = useState()
  const [products, setProducts] = useState([])
  //if (request_sent == false) {
    //getProducts()
    //initProducts()
    //request_sent = true;
  //}
  return (
    <div className="App">
      <div className="content">
        <h1>Suojavarustekauppa</h1>
        {MenuItems.MenuItems.map((item, index) => (
          <li key={index} onClick={() => setSelectedItem(item.title)}
          style={{cursor: "pointer"}}>{item.title}</li>
        ))}
        {selectedItem && (
          <h1 style={{textAlign: 'center'}}>{selectedItem}</h1>
        )}
        {selectedItem == 'Maskit' &&
          //products.map(products =>
            //<h1>{products.ProductName}</h1>

            //)

          <div><h1><GetProductsHtml></GetProductsHtml></h1></div>
        }

      </div>
    </div>

  );

  async function initProducts() {
    fetch(`http://127.0.0.1:5000/listProducts`)
        .then(response => response.json())
        .then(response => {
            setProducts(response.result);
            console.log(response.result);

    }
        )}

function GetProductsHtml() {
    initProducts()
    return products.map(products =>
      <div>
        <h1>{products.ProductName}</h1>
        <h1>{products.ProductDescription}</h1>
        </div>
    );
}
}

export default App;
