import './App.css';
import MenuItems from './components/Navbar/MenuItems';
import ReactDOM from 'react-dom'
import React, { useState } from 'react'
var request_sent = false;

function App() {

  const [selectedItem, setSelectedItem] = useState()
  const [products, setProducts] = useState({})
  if (request_sent == false) {
    getProducts();
    request_sent = true;
  }
  return (
    <div className="App">
      <div className="content">
        <h1>Suojavarustekauppa</h1>
        {MenuItems.MenuItems.map((item, index) => (
          <li key={index} onClick={() => setSelectedItem(item.title)}
          style={{cursor: "pointer"}}>{item.title}</li>
        ))}
        {selectedItem && (
          <h1 style={{textAlign: 'center'}}>{selectedItem}</h1>
        )}
        {selectedItem == 'Maskit' &&
          products.map(products =>
            <h1>{products.result.ProductName}</h1>
            )
          //<div><h1>{JSON.stringify(products.result)}</h1></div>
        }

      </div>
    </div>

  );

  function getProducts() {
    fetch('http://127.0.0.1:5000/listProducts')
    .then(res => res.json())
    .then(products => setProducts(products))
    .catch(console.log)
}
}

export default App;
